mapper.select:
select u.id,u.create_user,u.update_user,u.create_time,u.update_time, u.company_id,u.company_name,d.department_name as department_name,u.status,u.department_id, u.user_name,u.telphone,u.password,u.mobile,u.user_code,u.is_login_mybase, u.position_id, p.position_name as position_name,u.email from user u left join position p on u.position_id=p.id left join department d on u.department_id = d.id
where 1=1
and u.department_id in (${departmentId})
and u.company_id = #{companyId}
and u.user_name like '${userName}'
and u.telphone = #{telphone}
and u.`status` = #{status}
order by create_time desc

         select 

         from user
         where 1=1


		       and department_id =  #{departmentId}


		       and company_id =  #{companyId}



         order by create_time desc

mapper.update:
update user set 
status=100
where id=#{id} limit 1;
